import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import Joke from "./Joke";
import "./JokeList.css";

function JokeList({ numJokesToGet }) {
  const [jokes, setJokes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const getJokes = useCallback(async () => {
    try {
      // ... Your existing code to fetch jokes ...

      setJokes(jokes);
      setIsLoading(false);
    } catch (err) {
      console.error(err);
    }
  }, []);

  useEffect(() => {
    async function fetchData() {
      try {
        await getJokes();

        // ... rest of your code ...

      } catch (err) {
        console.error(err);
      }
    }

    fetchData();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [getJokes]);

  function generateNewJokes() {
    setIsLoading(true);
    getJokes();
  }

  function vote(id, delta) {
    setJokes((prevJokes) =>
      prevJokes.map((j) =>
        j.id === id ? { ...j, votes: j.votes + delta } : j
      )
    );
  }

  function resetVotesAndLocalStorage() {
    // Reset votes to 0 for all jokes
    setJokes((prevJokes) =>
      prevJokes.map((j) => ({ ...j, votes: 0 }))
    );

    // Clear local storage
    localStorage.clear();
  }

  let sortedJokes = [...jokes].sort((a, b) => b.votes - a.votes);

  if (isLoading) {
    return (
      <div className="loading">
        <i className="fas fa-4x fa-spinner fa-spin" />
      </div>
    );
  }

  return (
    <div className="JokeList">
      <button className="JokeList-getmore" onClick={generateNewJokes}>
        Get New Jokes
      </button>

      <button className="JokeList-reset" onClick={resetVotesAndLocalStorage}>
        Reset Votes and Clear Local Storage
      </button>

      {sortedJokes.map((j) => (
        <Joke text={j.joke} key={j.id} id={j.id} votes={j.votes} vote={vote} />
      ))}
    </div>
  );
}

export default JokeList;
